import { Alert } from 'bootstrap'
import React, { useState } from 'react'

import { Jumbotron, Container, Button, Form, FormGroup, Label, Input } from 'reactstrap'

function Cadastrar() {

    const [meta, setMeta] = useState({
        name: '',
        description: '',
        status: '',
    })

    const [response, setResponse] = useState({
        formSave: false,
        type: '',
        message: ''
    })

    const onChangeInput = event => setMeta({...meta, [event.target.name]: event.target.value})

    const sendMeta = async event => {
        event.preventDefault()

        setResponse({formSave: true})

        try{
            const res = await fetch('http://localhost:8080/metas', {
                method: 'POST',
                body: JSON.stringify(meta),
                headers: {'Content-Type': 'application/json'}
            })
            const responseEnv = await res.json()
            if (responseEnv.error) {
                setResponse({
                    formSave: false,
                    type: 'error',
                    message: responseEnv.message
                })
            } else {
                setResponse({
                    formSave: false,
                    type: 'sucess',
                    message: responseEnv.message
                })
            }
        } catch (err) {
            setResponse({
                formSave: false,
                type: 'error',
                message: 'Erro: Meta não cadastrada, tentar mais tarde.'
            })
        }
    }

    return (
        <>
            <Jumbotron fluid className="form">
                <style>
                    {`.form {
                        background-color: #171941;
                        color: #bf38ac;
                        padding-top: 30px;
                        padding-bottom: 150px;
                        margin-bottom: 0rem !important;
                    }`}
                </style>
                <Container>
                    <h1 className="display-4 text-center">Cadastrar Minha Meta</h1>
                    <hr />

                    {response.type === 'error' ? <Alert>{response.message}</Alert> : ""}
                    {response.type === 'sucess' ? <p>{response.message}</p> : ""}

                    <FormGroup onSubmit={sendMeta}>
                        <Label for="name">Meta </Label>
                        <Input type="text" name="name" id="name" 
                        placeholder="Aprender violão" 
                        onChange={onChangeInput}/>

                        <Label for="description">Descrição </Label>
                        <Input type="text" name="description" id="description" 
                        placeholder="Vou aprender violão em 3 meses" 
                        onChange={onChangeInput}/>

                        <Label for="status">Status </Label>
                        <Input type="text" name="status" id="status" 
                        placeholder="Pendente" 
                        onChange={onChangeInput}/>
                        <br />

                        {response.formSave ? 
                        <Button type="submit" color="danger"disabled>Enviando...</Button> :
                        <Button type="submit" outline color="primary">Cadastrar</Button>
                        }

                    </FormGroup>
                </Container>
            </Jumbotron>
        </>
    )
}

export default Cadastrar
