import React, { useState } from 'react'

import {Jumbotron} from 'reactstrap'

function Cadastrar() {

    const [meta, setMeta] = useState({
        name: '',
        description: '',
        status: '',
    })

    const [response, setResponse] = useState({
        formSave: false,
        type: '',
        message: ''
    })

    const onChangeInput = event => setMeta({...meta, [event.target.name]: event.target.value})

    const sendMeta = async event => {
        event.preventDefault()

        setResponse({formSave: true})

        try{
            const res = await fetch('http://localhost:8080/metas', {
                method: 'POST',
                body: JSON.stringify(meta),
                headers: {'Content-Type': 'application/json'}
            })
            const responseEnv = await res.json()
            if (responseEnv.error) {
                setResponse({
                    formSave: false,
                    type: 'error',
                    message: responseEnv.message
                })
            } else {
                setResponse({
                    formSave: false,
                    type: 'sucess',
                    message: responseEnv.message
                })
            }
        } catch (err) {
            setResponse({
                formSave: false,
                type: 'error',
                message: 'Erro: Meta n√£o cadastrada, tentar mais tarde.'
            })
        }
    }

    return (
        <>
            <Jumbotron fluid className="form">
                <style>
                    {`.form {
                        backgroun
                    }`}
                </style>
                <h1>Cadastrar Minha Meta</h1>
                <hr />

                {response.type === 'error' ? <p>{response.message}</p> : ""}
                {response.type === 'sucess' ? <p>{response.message}</p> : ""}

                <form onSubmit={sendMeta}>
                    <label>Meta </label>
                    <input type="text" name="name" id="name" 
                    placeholder="Aprender viol√£o" 
                    onChange={onChangeInput}/>
                    <br />

                    <label>Descri√ß√£o </label>
                    <input type="text" name="description" id="description" 
                    placeholder="Vou aprender viol√£o em 3 meses" 
                    onChange={onChangeInput}/>
                    <br />

                    <label>Status </label>
                    <input type="text" name="status" id="status" 
                    placeholder="Pendente" 
                    onChange={onChangeInput}/>
                    <br />

                    {response.formSave ? 
                    <button type="submit" disabled>Enviando...</button> :
                    <button type="submit">Cadastrar</button>
                    }

                </form>
            </Jumbotron>
        </>
    )
}

export default Cadastrar
